<script>
import { ref } from "vue";
import { useStore } from "vuex";
import {
    SIDEBAR_MODULE,
    SIDEBAR_SET_SLIDEIN,
    TOASTER_MESSAGE,
    TOASTER_MODULE,
} from "../../store/types/types";

import { useRouter } from "vue-router";
export default {
    name: "MainHeader",
    components: {},
    setup(props, context) {
        const store = useStore();
        const router = useRouter();
        const featureunavailable = (feature) => {
            store.dispatch(`${TOASTER_MODULE}${TOASTER_MESSAGE}`, {
                message: feature + " feature not yet implemented.",
                ttl: 5,
                type: "success",
            });
        };

        const slideinMenu = () => {
            store.commit(`${SIDEBAR_MODULE}${SIDEBAR_SET_SLIDEIN}`);
        };

        const showbarcodemodal = () => {
            featureunavailable("Barcode");
        };

        const neworder = () => {
            router.push({
                name: "NewOrder",
                params: {},
            });
        };
        return {
            neworder,
            slideinMenu,
            showbarcodemodal,
        };
    },
};
</script>

